exports.raycontains = function(rayIntersects, meshName) {
  var rayItemName;
  var meshInRay = false;

  rayIntersects.every((item,i) => {
    rayItemName = item.object.name;
    if(rayItemName == meshName){
      meshInRay = true;
      return false;
    }else{
      return true;
    }
  });

  return meshInRay;
}
